package com.example.hypelib.domain;import lombok.*;import javax.persistence.*;@Entity@NoArgsConstructor@RequiredArgsConstructorpublic class Book {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    @Getter    @Setter    private Long id;    @Getter    @Setter    @NonNull    private String title;    @Getter    @Setter    @NonNull    private String author;    @Getter    @Setter    @NonNull    private String yearOfPublication;    @Getter    @Setter    @NonNull    private String description;    @Getter    @Setter    @NonNull    private boolean read;    @ManyToOne(fetch = FetchType.EAGER)    @JoinColumn(name = "user_id")    @Getter    @Setter    @NonNull    private User publisher;    public String getPublisherName() {        return publisher.getUsername();    }}